frames.customCss:
/* hide the menu bar "Calendar" text and remove minimum width */
div[style*="min-width: 238px"] {
    min-width: 0 !important;
    padding-right: 0 !important;
}
div[style*="min-width: 238px"] span[role*="heading"] {
    display: none !important;
}
/*
hide the left sidebar
*/
div.QQYuzf[jsname='QA0Szd']
{
    display: none !important;
}
/*
hide the right sidebar
*/
div.Kk7lMc-DWWcKd-OomVLb-haAclf.gboEAb[role='complementary'][data-keeps-details-open='true'] {
    display: none !important;
}
/*
hide the add button
*/
div.dwlvNd[jscontroller='kcxPef'][data-is-drawer-closed='false'] {
    display: none !important;
}
